01背包模板话不多说，上代码！！
#include&lt;bits&#x2F;stdc++.h&gt;
using namespace std;
int main()
&#123;
    int V, n;
	cin&gt;&gt;V&gt;&gt;n;
    &#x2F;&#x2F;输入背包容量及物品数量
	int v[n + 1], w[n + 1], dp[V + 1];
    &#x2F;&#x2F;vi表示第i个物品的体积
    &#x2F;&#x2F;wi表示第i个物品的价值
    &#x2F;&#x2F;dpi表示花费i体积所能获得的最大价值
	for(int i &#x3D; 1; i &lt;&#x3D; n; i++)
		cin&gt;&gt;v[i]&gt;&gt;w[i];
	memset(dp, 0, sizeof(dp));
    &#x2F;&#x2F;初始化
	for(int i &#x3D; 1; i &lt;&#x3D; n; i++)
    &#x2F;&#x2F;对于每个物品有取或不取两种选择
    &#x2F;&#x2F;因此枚举正在对第i个物品进行决策
	&#123;
  		for(int j &#x3D; V; j &gt;&#x3D; v[i]; j--)
        &#x2F;&#x2F;枚举第i个物品所花费的体积
        &#x2F;&#x2F;从后往前使得上一个物品的决策仍可以使用
		&#123;
			dp[j] &#x3D; max(dp[j - v[i]] + w[i], dp[j]);
		&#125;
	&#125;
	cout&lt;&lt;dp[V];
    &#x2F;&#x2F;输出
&#125;


