const long long int MAXN=19+1,INF=9223372036854775807LL; // INT64_MAX

long long int n,xx,ans=INF;
auto f(int deepth, long long int x)
{
  bool st[12]={0};// 桶自动去重
  int cnt=0; // 统计数位
  for(long long int y=x;y;++cnt,st[y%10]=true,y/=10);
