template &lt;typename T&gt;
void Merge(int l1, int r1, int l2, int r2, vector&lt;T&gt; &amp;a)
// in this FUNC, we will mix numbers in [l,r)
{
  vector&lt;T&gt; t;
  // mix them by compare to sort
  int i = l1, j = l2;
  while (i &lt; r1 &amp;&amp; j &lt; r2)
    t.push_back(a[i] &lt;= a[j] ? a[i++] : a[j++]);

  // last step will leave some numbers
  // so do it now
  while (i &lt; r1)
    t.push_back(a[i++]);
  while (j &lt; r2)
    t.push_back(a[j++]);

  // copy the sorted vector into the origan vector
  for (int i = l1; i &lt; r2; ++i)
    a[i] = t[i - l1];
}
template &lt;typename T&gt;
void MergeSort(int l, int r, vector&lt;T&gt; &amp;a)
// in this FUNC, we will sort numbers in [l,r)
{
  // one number is always sorted
  if (r - l &lt;= 1)
    return;

  int mid = (l + r) &gt;&gt; 1;

  // sort the left
  MergeSort(l, mid, a);
  // sort the right
  MergeSort(mid, r, a);

  Merge(l, mid, r, a);

  return;
}
