// you know~
template &lt;typename T&gt;
void MergeSort(int l, int r, vector&lt;T&gt; &amp;a)
// in this FUNC, we will sort numbers in [0,n)
{
  for (int stepDistance = 1; stepDistance &lt; r; stepDistance += stepDistance)
  {
    for (int start = l; start &lt; r; start += (stepDistance &lt;&lt; 1))
    // start is the start index of the scope which will be sorting
    {
      int end = min(start + (stepDistance &lt;&lt; 1), r),
          // the end index of the scope which will be sorting
          mid = min(start + stepDistance, r);
      // the middle index of the scope which will be sorting
      Merge(start, mid, mid, end, a);
    }
  }
}

